 5namespace Drupal\Tests\arsapps_module\Functional;
 6
 7use Drupal\Tests\BrowserTestBase;
 8
 9/**
10 * Tests for ARSApps Module functionality.
11 *
12 * @group arsapps_module
13 */
14final class ArsappsModuleTest extends BrowserTestBase {
15
16  /**
17   * {@inheritdoc}
18   */
19  protected $defaultTheme = 'stark';
20
21  /**
22   * {@inheritdoc}
23   */
24  protected static $modules = ['arsapps_module'];
25
26  /**
27   * Tests the module installation.
28   */
29  public function testModuleInstallation(): void {
30    $this->assertTrue(\Drupal::moduleHandler()->moduleExists('arsapps_module'));
31  }
32
33  /**
34   * Tests the settings form.
35   */
36  public function testSettingsForm(): void {
37    $admin_user = $this->drupalCreateUser(['administer site configuration']);
38    $this->drupalLogin($admin_user);
39
40    $this->drupalGet('/admin/config/system/arsapps-module');
41    $this->assertSession()->statusCodeEquals(200);
42    $this->assertSession()->pageTextContains('ARSApps Module Settings');
43    $this->assertSession()->fieldExists('example_text');
44    $this->assertSession()->fieldExists('example_number');
45    $this->assertSession()->fieldExists('example_boolean');
46  }
47
48  /**
49   * Tests the info page.
50   */
51  public function testInfoPage(): void {
52    $user = $this->drupalCreateUser(['access content']);
53    $this->drupalLogin($user);
54
55    $this->drupalGet('/arsapps-module/info');
56    $this->assertSession()->statusCodeEquals(200);
57    $this->assertSession()->pageTextContains('ARSApps Module Information');
58    $this->assertSession()->pageTextContains('placeholder module');
59  }
60
61  /**
62   * Tests the block plugin.
63   */
64  public function testBlockPlugin(): void {
65    $admin_user = $this->drupalCreateUser(['administer blocks']);
66    $this->drupalLogin($admin_user);
67
68    $this->drupalGet('/admin/structure/block');
69    $this->assertSession()->statusCodeEquals(200);
70    $this->clickLink('Place block');
71    $this->assertSession()->pageTextContains('ARSApps Module Block');
72  }
73
74  /**
75   * Tests the help page.
76   */
77  public function testHelpPage(): void {
78    $admin_user = $this->drupalCreateUser(['access administration pages']);
79    $this->drupalLogin($admin_user);
80
81    $this->drupalGet('/admin/help/arsapps_module');
82    $this->assertSession()->statusCodeEquals(200);
83    $this->assertSession()->pageTextContains('About');
84    $this->assertSession()->pageTextContains('ARSApps Module is a placeholder');
85  }
86
87  /**
88   * Tests configuration schema.
89   */
90  public function testConfigurationSchema(): void {
91    $config = \Drupal::config('arsapps_module.settings');
92    $this->assertNotNull($config->get('example_text'));
93    $this->assertNotNull($config->get('example_number'));
94    $this->assertNotNull($config->get('example_boolean'));
95  }
96
97}

