 5namespace Drupal\arsapps_module\Form;
 6
 7use Drupal\Core\Form\ConfigFormBase;
 8use Drupal\Core\Form\FormStateInterface;
 9
10/**
11 * Configure ARSApps Module settings for this site.
12 */
13final class ArsappsModuleSettingsForm extends ConfigFormBase {
14
15  /**
16   * {@inheritdoc}
17   */
18  public function getFormId(): string {
19    return 'arsapps_module_settings';
20  }
21
22  /**
23   * {@inheritdoc}
24   */
25  protected function getEditableConfigNames(): array {
26    return ['arsapps_module.settings'];
27  }
28
29  /**
30   * {@inheritdoc}
31   */
32  public function buildForm(array $form, FormStateInterface $form_state): array {
33    $config = $this->config('arsapps_module.settings');
34
35    $form['example_text'] = [
36      '#type' => 'textfield',
37      '#title' => $this->t('Example text field'),
38      '#default_value' => $config->get('example_text'),
39      '#description' => $this->t('This is a placeholder text field for testing purposes.'),
40      '#maxlength' => 255,
41    ];
42
43    $form['example_number'] = [
44      '#type' => 'number',
45      '#title' => $this->t('Example number field'),
46      '#default_value' => $config->get('example_number'),
47      '#description' => $this->t('This is a placeholder number field for testing purposes.'),
48      '#min' => 0,
49      '#max' => 100,
50    ];
51
52    $form['example_boolean'] = [
53      '#type' => 'checkbox',
54      '#title' => $this->t('Example boolean field'),
55      '#default_value' => $config->get('example_boolean'),
56      '#description' => $this->t('This is a placeholder boolean field for testing purposes.'),
57    ];
58
59    $form['example_textarea'] = [
60      '#type' => 'textarea',
61      '#title' => $this->t('Example textarea field'),
62      '#default_value' => $config->get('example_textarea'),
63      '#description' => $this->t('This is a placeholder textarea field for testing purposes.'),
64      '#rows' => 4,
65    ];
66
67    return parent::buildForm($form, $form_state);
68  }
69
70  /**
71   * {@inheritdoc}
72   */
73  public function submitForm(array &$form, FormStateInterface $form_state): void {
74    $this->config('arsapps_module.settings')
75      ->set('example_text', $form_state->getValue('example_text'))
76      ->set('example_number', $form_state->getValue('example_number'))
77      ->set('example_boolean', $form_state->getValue('example_boolean'))
78      ->set('example_textarea', $form_state->getValue('example_textarea'))
79      ->save();
80
81    parent::submitForm($form, $form_state);
82  }
83
84}
85

