 3/**
 4 * @file
 5 * Functions to support theming in the ARSApps Theme.
 6 */
 7
 8use Drupal\Component\Utility\Html;
 9use Drupal\Core\Form\FormStateInterface;
10
11/**
12 * Implements hook_preprocess_HOOK() for HTML document templates.
13 */
14function arsapps_theme_preprocess_html(&$variables) {
15  // Add custom body classes.
16  $current_path = \Drupal::service('path.current')->getPath();
17  $path_alias = \Drupal::service('path_alias.manager')->getAliasByPath($current_path);
18  $path_alias = ltrim($path_alias, '/');
19
20  if (!empty($path_alias)) {
21    $variables['attributes']['class'][] = 'path--' . Html::cleanCssIdentifier($path_alias);
22  }
23}
24
25/**
26 * Implements hook_preprocess_HOOK() for page templates.
27 */
28function arsapps_theme_preprocess_page(&$variables) {
29  // Add page-specific variables here.
30  $variables['site_name'] = \Drupal::config('system.site')->get('name');
31  $variables['site_slogan'] = \Drupal::config('system.site')->get('slogan');
32}
33
34/**
35 * Implements hook_preprocess_HOOK() for node templates.
36 */
37function arsapps_theme_preprocess_node(&$variables) {
38  // Add node-specific preprocessing here.
39  $node = $variables['node'];
40  $variables['created_formatted'] = \Drupal::service('date.formatter')->format($node->getCreatedTime(), 'medium');
41}
42
43/**
44 * Implements hook_theme_suggestions_HOOK_alter() for page templates.
45 */
46function arsapps_theme_theme_suggestions_page_alter(array &$suggestions, array $variables) {
47  // Add custom page template suggestions.
48  if ($node = \Drupal::routeMatch()->getParameter('node')) {
49    $content_type = $node->bundle();
50    $suggestions[] = 'page__' . $content_type;
51  }
52}
53
54/**
55 * Implements hook_form_alter().
56 */
57function arsapps_theme_form_alter(&$form, FormStateInterface $form_state, $form_id) {
58  // Add custom form alterations here.
59  switch ($form_id) {
60    case 'search_block_form':
61      $form['keys']['#attributes']['placeholder'] = t('Search...');
62      break;
63  }
64}
65

