file.content:
/**
 * @file
 * Main stylesheet for ARSApps Theme.
 */

/* Base styles */
body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  line-height: 1.6;
  color: #333;
  margin: 0;
  padding: 0;
}

/* Layout container */
.layout-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

/* Header */
header[role="banner"] {
  background-color: #f8f9fa;
  padding: 1rem 0;
  border-bottom: 1px solid #dee2e6;
}

/* Main content area */
main[role="main"] {
  display: flex;
  gap: 2rem;
  padding: 2rem 0;
}

.layout-content {
  flex: 1;
}

/* Sidebars */
.layout-sidebar-first,
.layout-sidebar-second {
  flex: 0 0 250px;
  background-color: #f8f9fa;
  padding: 1rem;
  border-radius: 4px;
}

/* Footer */
footer[role="contentinfo"] {
  background-color: #343a40;
  color: #fff;
  padding: 2rem 0;
  margin-top: 3rem;
}

/* Skip link */
.skip-link {
  position: absolute;
  top: -40px;
  left: 6px;
  z-index: 1000;
  color: #fff;
  background-color: #000;
  padding: 8px 16px;
  text-decoration: none;
  border-radius: 0 0 4px 4px;
}

.skip-link:focus {
  top: 0;
}

/* Responsive design */
@media (max-width: 768px) {
  main[role="main"] {
    flex-direction: column;
  }

  .layout-sidebar-first,
  .layout-sidebar-second {
    flex: none;
  }
}

