{#
/**
 * @file
 * Default theme implementation to display a single page.
 *
 * Available variables:
 * - page: A list of page regions and their content.
 */
#}
<!DOCTYPE html>
<html{{ html_attributes.addClass('no-js').setAttribute('lang', html_attributes.offsetGet('lang') ?: 'en').setAttribute('dir', html_attributes.offsetGet('dir') ?: 'ltr') }}>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1">
  {{ head }}
  {{ page_top }}
</head>
<body{{ attributes.addClass('bg-white text-gray-900 font-sans antialiased') }}>
  <div class="min-h-screen flex flex-col">

    {# Header #}
    {% if page.header %}
      <header class="bg-white shadow-sm border-b border-gray-200" role="banner">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {{ page.header }}
        </div>
      </header>
    {% endif %}

    {# Main Menu #}
    {% if page.primary_menu %}
      <nav class="bg-blue-600" role="navigation" aria-label="{{ 'Main Menu'|t }}">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {{ page.primary_menu }}
        </div>
      </nav>
    {% endif %}

    {# Breadcrumb #}
    {% if page.breadcrumb %}
      <div class="bg-gray-50 border-b border-gray-200">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-2">
          {{ page.breadcrumb }}
        </div>
      </div>
    {% endif %}

    {# Main Content Area #}
    <main class="flex-1" role="main">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {# Highlighted Content #}
        {% if page.highlighted %}
          <div class="mb-8">
            {{ page.highlighted }}
          </div>
        {% endif %}

        {# Help Text #}
        {% if page.help %}
          <div class="mb-8 bg-blue-50 border border-blue-200 rounded-md p-4">
            {{ page.help }}
          </div>
        {% endif %}

        <div class="grid grid-cols-1 lg:grid-cols-4 gap-8">

          {# Sidebar First #}
          {% if page.sidebar_first %}
            <aside class="lg:col-span-1" role="complementary" aria-label="{{ 'Sidebar first'|t }}">
              {{ page.sidebar_first }}
            </aside>
          {% endif %}

          {# Main Content #}
          <div class="{% if page.sidebar_first and page.sidebar_second %}lg:col-span-2{% elseif page.sidebar_first or page.sidebar_second %}lg:col-span-3{% else %}lg:col-span-4{% endif %}">
            {{ page.content }}
          </div>

          {# Sidebar Second #}
          {% if page.sidebar_second %}
            <aside class="lg:col-span-1" role="complementary" aria-label="{{ 'Sidebar second'|t }}">
              {{ page.sidebar_second }}
            </aside>
          {% endif %}
        </div>
      </div>
    </main>

    {# Footer #}
    {% if page.footer %}
      <footer class="bg-gray-800 text-white mt-auto" role="contentinfo">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {{ page.footer }}
        </div>
      </footer>
    {% endif %}

  </div>
  {{ page_bottom }}
</body>
</html>

