{#
/**
 * @file
 * Default theme implementation to display a single page.
 *
 * Available variables:
 * - page: A list of page regions and their content.
 */
#}
<div class="min-h-screen flex flex-col bg-white">

  {# Header Region #}
  {% if page.header %}
    <header class="bg-white shadow-sm border-b border-gray-200" role="banner">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {{ page.header }}
      </div>
    </header>
  {% endif %}

  {# Navigation Region #}
  {% if page.navigation %}
    <nav class="bg-usda-blue-600 text-white" role="navigation" aria-label="{{ 'Main navigation'|t }}">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {{ page.navigation }}
      </div>
    </nav>
  {% endif %}

  {# Hero Banner Region #}
  {% if page.hero_banner %}
    <section class="relative bg-gradient-to-r from-green-50 to-blue-50" aria-label="{{ 'Hero banner'|t }}">
      {{ page.hero_banner }}
    </section>
  {% endif %}

  {# Breadcrumb Region #}
  {% if page.breadcrumb %}
    <div class="bg-gray-50 border-b border-gray-200">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-2">
        <nav aria-label="{{ 'Breadcrumb'|t }}">
          {{ page.breadcrumb }}
        </nav>
      </div>
    </div>
  {% endif %}

  {# Highlighted Region #}
  {% if page.highlighted %}
    <section class="bg-yellow-50 border-l-4 border-yellow-400">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
        {{ page.highlighted }}
      </div>
    </section>
  {% endif %}

  {# Help Region #}
  {% if page.help %}
    <section class="bg-blue-50 border border-blue-200">
      <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
        {{ page.help }}
      </div>
    </section>
  {% endif %}

  {# Main Content Wrapper #}
  <main class="flex-1" role="main">

    {# Main Content Region #}
    {% if page.content %}
      <section class="py-8">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div class="grid grid-cols-1 lg:grid-cols-4 gap-8">

            {# Sidebar First #}
            {% if page.sidebar_first %}
              <aside class="lg:col-span-1 order-first lg:order-none" role="complementary" aria-label="{{ 'Primary sidebar'|t }}">
                {{ page.sidebar_first }}
              </aside>
            {% endif %}

            {# Main Content #}
            <div class="{% if page.sidebar_first and page.sidebar_second %}lg:col-span-2{% elseif page.sidebar_first or page.sidebar_second %}lg:col-span-3{% else %}lg:col-span-4{% endif %}">
              {{ page.content }}
            </div>

            {# Sidebar Second #}
            {% if page.sidebar_second %}
              <aside class="lg:col-span-1" role="complementary" aria-label="{{ 'Secondary sidebar'|t }}">
                {{ page.sidebar_second }}
              </aside>
            {% endif %}
          </div>
        </div>
      </section>
    {% endif %}

    {# Online Resources Region #}
    {% if page.online_resources %}
      <section class="py-12 bg-gray-50" aria-labelledby="online-resources-heading">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 id="online-resources-heading" class="sr-only">{{ 'Online Resources'|t }}</h2>
          {{ page.online_resources }}
        </div>
      </section>
    {% endif %}

    {# Staff Picks Region #}
    {% if page.staff_picks %}
      <section class="py-12 bg-usda-blue-900 text-white" aria-labelledby="staff-picks-heading">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 id="staff-picks-heading" class="sr-only">{{ 'Staff Picks'|t }}</h2>
          {{ page.staff_picks }}
        </div>
      </section>
    {% endif %}

    {# Employee CTA Region #}
    {% if page.employee_cta %}
      <section class="py-8 bg-green-600 text-white" aria-labelledby="employee-cta-heading">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 id="employee-cta-heading" class="sr-only">{{ 'USDA Employee Resources'|t }}</h2>
          {{ page.employee_cta }}
        </div>
      </section>
    {% endif %}

  </main>

  {# Footer Region #}
  <footer class="bg-gray-800 text-white mt-auto" role="contentinfo">
    <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

      {# Main Footer Content #}
      {% if page.footer_first or page.footer_second or page.footer_third %}
        <div class="py-12">
          <div class="grid grid-cols-1 md:grid-cols-3 gap-8">

            {% if page.footer_first %}
              <div class="footer-column">
                {{ page.footer_first }}
              </div>
            {% endif %}

            {% if page.footer_second %}
              <div class="footer-column">
                {{ page.footer_second }}
              </div>
            {% endif %}

            {% if page.footer_third %}
              <div class="footer-column">
                {{ page.footer_third }}
              </div>
            {% endif %}
          </div>
        </div>
      {% endif %}

      {# Footer Bottom #}
      {% if page.footer_bottom %}
        <div class="border-t border-gray-700 py-6">
          {{ page.footer_bottom }}
        </div>
      {% endif %}
    </div>
  </footer>

</div>

