/**
 * Implements hook_form_system_theme_settings_alter().
 */
function arsapps_theme_form_system_theme_settings_alter(array &$form, FormStateInterface $form_state): void {

input.command:
drush eval "
\$entity_type_manager = \Drupal::entityTypeManager();
\$field_manager = \Drupal::service('entity_field.manager');

// Add the layout_builder__layout field to the page content type
\$field_storage_config = \$entity_type_manager->getStorage('field_storage_config');
\$field_config = \$entity_type_manager->getStorage('field_config');

// Create field storage if it doesn't exist
if (!\$field_storage_config->load('node.layout_builder__layout')) {
  \$field_storage = \$field_storage_config->create([
    'field_name' => 'layout_builder__layout',
    'entity_type' => 'node',
    'type' => 'layout_section',
    'cardinality' => -1,
  ]);
  \$field_storage->save();
  echo 'Created layout_builder__layout field storage\n';
}

// Create field instance for page content type
if (!\$field_config->load('node.page.layout_builder__layout')) {
  \$field = \$field_config->create([
    'field_storage' => \$field_storage_config->load('node.layout_builder__layout'),
    'bundle' => 'page',
    'label' => 'Layout',
  ]);
  \$field->save();
  echo 'Created layout_builder__layout field for page content type\n';
}

// Clear field caches
\$field_manager->clearCachedFieldDefinitions();
echo 'Field definitions cleared';
"

drush eval "
\$node = \Drupal\node\Entity\Node::create([
  'type' => 'page',
  'title' => 'Home',
  'status' => 1,
  'body' => [
    'value' => 'Welcome to the National Agricultural Library website.',
    'format' => 'basic_html'
  ]
]);
\$node->save();
echo 'Created node: ' . \$node->id();
"

drush eval "
\$entity_definition_update_manager = \Drupal::entityDefinitionUpdateManager();
\$entity_type_manager = \Drupal::entityTypeManager();

// Get the node entity type
\$node_type = \$entity_type_manager->getDefinition('node');

// Install Layout Builder field
\$field_storage_definitions = \$entity_definition_update_manager->getFieldStorageDefinitions('node');
if (!isset(\$field_storage_definitions['layout_builder__layout'])) {
  echo 'Installing Layout Builder field storage...';
  \$entity_definition_update_manager->installFieldStorageDefinition('layout_builder__layout', 'node', 'layout_builder', [
    'field_name' => 'layout_builder__layout',
    'entity_type' => 'node',
    'type' => 'layout_section',
    'cardinality' => -1,
    'provider' => 'layout_builder',
    'schema' => [
      'columns' => [
        'section' => [
          'type' => 'blob',
          'size' => 'normal',
          'serialize' => TRUE,
        ],
      ],
    ],
  ]);
  echo 'Layout Builder field installed';
} else {
  echo 'Layout Builder field already exists';
}
"
